import React, { Component } from 'react';

export default class MonthlyGoals extends React.Component {

    constructor()
    {
        super();
        this.state = {

        }
    }

    buildList()
    {
        const ls_detail = this.props.goals_list.map( goal => (<h4> {goal.id + ". " + goal.title} </h4>));

        const appearingdisplay = {
            display: "block", 
            width: "100%", 
            height: "100%", 

        }

        const dissapearingdisplay = {
            display: "none",
            width: "100%", 
            height: "100%"
        }

        return (
            <ul style={this.state.collapsed ? dissapearingdisplay : appearingdisplay}>
                {ls_detail}
            </ul>
        );
    }

    render() {
        return (
            <div className="Monthly_Goals">
                <h2>Monthly Goals</h2>
                {this.buildList()}
            </div>
        )
    }
}
